import { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import jobService from '../services/job.service';
import UserService from '../services/User.service';

const UserPage = () => {
  const[appliedjob,setAppliedJobs]=useState([]);
  const [job, setJobs] = useState([]);
  const [search,setSearch]=useState([]);
  const [search1,setSearch1]=useState([]);


  const init = () => {
    jobService.getAll()
      .then(response => {
        console.log('Printing job data', response.data);
        setJobs(response.data);
      })
      .catch(error => {
        console.log('Something went wrong', error);
      }) 
  }
  const applyJob = (id) => {
    console.log('Printing id', id);
    if(window.confirm('are you sure you want to apply'))
    UserService.create(job)
      .then(response => {
        console.log('job applied successfully', response.data);
        init();
      })
      .catch(error => {
        console.log('Something went wrong', error);
      })
  }


  useEffect(()=> {
    init();
  }, []);


  return (
    <div className="container">
      <h3>List of jobs</h3>
      <div>

            <form >
                <input width="100px" type="text" placeholder="Search Job by Companyname " value={search} onChange={(e)=>
                    setSearch(e.target.value)}/><br/><br/>   
            </form> 
            <form >
                <input width="100px" type="text" placeholder="Search Job by Location " value={search1} onChange={(e)=>
                    setSearch1(e.target.value)}/><br/><br/> 
            </form> 
            {job.filter((item)=>
                       item.location.toLowerCase().includes(search1)).map((job)=>(
                           <div className='col-md-6'>
                               <div className='card'>
                               <div className='card-body'>
                                   <h5 className='card-title'>{job.companyname}</h5>
                              </div>
                            <br/>
                       </div>
                       <br/>
                       </div>)
                       )}  

               <div className='container'>
                   <div className='row'>
                   {job.filter((item)=>
                       item.companyname.toLowerCase().includes(search)).map((job)=>(
                           <div className='col-md-6'>
                               <div className='card'>
                               <div className='card-body'>
                                   <h5 className='card-title'>{job.companyname}</h5>
                                   <h5 className='card-title'>{job.designation}</h5>
                              </div>
                            <br/>
                       </div>
                       <br/>
                       </div>)
                       )}
                   </div>
               </div> 
               <div className='spinner-border text-primary'>
               </div>  
        </div>

      <hr/>
      <div>
        <table className="table table-bordered table-striped">
          <thead className="thead-dark">
            <tr>
              <th>Logo</th>
              <th>Company Name</th>
              <th>Desgination</th>
              <th>Yrs of Exp</th>
              <th>Job description</th>
              <th>Location</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
          {
            job.map(job => (
              <tr key={job.id}>
                <td align="center"><img src={job.logo} width="50" height="45" alt="Company logo"/></td>
                <td>{job.companyname}</td>
                <td>{job.designation}</td>
                <td>{job.yrsofexp}</td>
                <td>{job.jobdesc}</td>
                <td>{job.location}</td>
                <td>
                  <a href="/finalPage" className="btn btn-success" onClick={()=>{
                      applyJob(job.id);
                  }}> Apply </a>
                </td>
              </tr>
            ))
          }
          </tbody>
        </table>

      </div>
    </div>
  );
}

export default UserPage
